'use strict';var JsonFile=require('../JsonFile');describe('JsonFile',function(){it('is a class',function(){var file=new JsonFile('../package.json');expect(file instanceof JsonFile).toBe(true);});it('has static functions',function(){expect(JsonFile.readAsync).toBeDefined();expect(JsonFile.writeAsync).toBeDefined();});pit('reads JSON from a file',function(){var file=new JsonFile('./package.json');return file.readAsync().then(function(object){expect(object.version).toBeDefined();});});pit('reads JSON statically from a file',function(){return JsonFile.readAsync('./package.json').then(function(object){expect(object.version).toBeDefined();});});pit('reads JSON5 from a file',function(){var file=new JsonFile('./test-json5.json',{json5:true});return file.readAsync().then(function(object){expect(object.itParsedProperly).toBe(42);});});});