'use strict';var JsonFileError=require('../JsonFileError');describe('JsonFileError',function(){it('is an error',function(){var error=new JsonFileError('Example');expect(error instanceof Error).toBe(true);expect(error instanceof JsonFileError).toBe(true);});it('has a flag that says it\'s a JsonFileError',function(){var error=new JsonFileError('Example');expect(error.isJsonFileError).toBe(true);});it('includes its cause',function(){var cause=new Error('Root cause');var error=new JsonFileError('Example',cause);expect(error.cause).toBe(cause);expect(error.message).toMatch(cause.message);});});